function GetUsers(n){$.ajax({url:n,type:"post",success:function(n){$("#content").html(n);$("#btnUsers").addClass("active");$("#btnRoles").removeClass("active")},error:function(){alert("Oops, hemos tenido un problema...")}})}function GetRoles(n){$.ajax({url:n,type:"post",success:function(n){$("#content").html(n);$("#btnUsers").removeClass("active");$("#btnRoles").addClass("active")},error:function(){alert("Oops, hemos tenido un problema...")}})}function saveNewRole(n,t){if(!$("#roleName").val()){alert("Introduce un nombre para el rol");return}$.ajax({url:n,data:{roleName:$("#roleName").val()},type:"post",success:function(n){$("#listaRolesUpd").append(new Option($("#roleName").val(),n,!1,!0));$("#listaRolesDel").append(new Option($("#roleName").val(),n,!1,!0));getClaims(t)},error:function(){alert("Oops, hemos tenido un problema...")}})}function removeRole(n,t){if(!$("#listaRolesDel").val()){alert("Para poder borrar un rol primero tienes que seleccionarlo");return}$.ajax({url:n,data:{roleId:$("#listaRolesDel").val()},type:"post",success:function(){$(".desplegables option[value="+$("#listaRolesDel").val()+"]").remove();getClaims(t)},error:function(){alert("Oops, hemos tenido un problema...")}})}function getClaims(n){if(!$("#listaRolesUpd").val()){$("#claims").html("");return}$.ajax({url:n,data:{roleId:$("#listaRolesUpd").val()},type:"post",success:function(n){$("#claims").html(n)},error:function(){alert("Oops, hemos tenido un problema...")}})}